# given a sentence return the average word length. 

class Solution:

    def average_word_length(self,sentence):
        for car in "!?',;.“’_":
            sentence = sentence.replace(car, '')
        words = sentence.split()
        return round(sum(len(word) for word in words)/len(words),2)

s1 = Solution()

